var lastli=-1;var num=0;var mode=1;$(function(){$("#audio")[0].onerror=function(){window.parent.play_next()};$(window).scroll(function(){let htmlHeight=$(document).height();let clientHeight=$(window).height();let scrollTop=$(document).scrollTop();let he=scrollTop+clientHeight;if(he>=htmlHeight*0.98&&!window.parent.endOffset&&table.songs!=null&&table.songs!=undefined&&table.songs.length<200){window.parent.nextPage()}});document.onclick=function(){window.parent.$("#selecttime,#selectmode").css("display","none")};document.onkeydown=function(event){let e=event||window.event||arguments.callee.caller.arguments[0];if(e&&e.keyCode==32){play_pause();e.preventDefault()}};window.parent.$('#search_button').click()});var table=new Vue({el:'#list',data:{songs:{}},methods:{toTime:function(sec){return convertTime(sec/1000)},playSong:function(mode,idx){changeSrc(mode,idx)},page:function(arg){offset=$("#page").text();if(arg=='last'&&offset>1){offset--;getMusicList()}else if(arg=='next'){offset++;getMusicList()}}}});function playSong(id){let type=window.parent.type;let url=encodeURI('/getMusicUrl?param='+id+'&type='+type);$.ajax({url:url,success:function(result){if(result=="-1"||!(result.indexOf('mp3')>=0||result.indexOf('m4a')>=0)){window.parent.play_next()}else{$("#audio").attr("src",result);$('#audio')[0].pause();window.parent.play_pause();getLyric(num);window.parent.ended=false}}})}function changeSrc(mode,num){if(table.songs==undefined)return;let vinylimg=window.parent.$("#vinylimg");let audioinfo=window.parent[1].$("#audioinfo");let bg=window.parent.$('#bg');let bgLow=window.parent.$('#bg-low');if(mode!=0){playmode(this.mode,num);num=this.num}let song=table.songs[num];window.parent.$("#song_name").html('<marquee behavior="alternate" truespeed="truespeed" scrolldelay="100" scrollamount="2">'+song.artist+' - '+song.title+'</marquee>');audioinfo.find('.span1').html(song.title);audioinfo.find('.span2').html('歌手：<span onclick="jumplink(this)">'+(song.artist||'未知歌手')+'</span>');audioinfo.find('.span3').html('专辑：<span onclick="jumplink(this)">'+(song.album||'未知专辑')+'</span>');if(song.image){bgLow.css('background-image','url(\"'+song.image+'\")');bg.css('background-image','url(\"'+song.image+'\")');vinylimg.attr('src',song.image)}else if(window.parent.type=='kugou'){getAlbumImg(song.id)}else{bgLow.css('background-image',null);bg.css('background-image',null);vinylimg.attr('src','../image/novinyl.png');window.parent.$('#vinyl').css('backgroundImage','url(../image/Vinyl_'+window.parent.type+'.png)')}if(lastli!=-1){$("#li"+(lastli+1)+" td:eq(1)").removeAttr("style");$("#li"+(lastli+1)+" td:eq(0)").removeAttr("value")}$("#li"+(num+1)+" td:eq(1)").css("color","rgb("+window.parent.color+")");$("#li"+(num+1)+" td:eq(0)").attr("value","gif_"+window.parent.type);this.num=num;lastli=num;playSong(song.id)}function getAlbumImg(id){let url=encodeURI('/getAlbumImg?param='+id);$.ajax({url:url,success:function(result){let bg=window.parent.$('#bg');let bgLow=window.parent.$('#bg-low');let vinylimg=window.parent.$("#vinylimg");if(result!=null&&result!=''&&result!=-1){bgLow.css('background-image','url(\"'+result+'\")');bg.css('background-image','url(\"'+result+'\")');vinylimg.attr('src',result)}else{bgLow.css('background-image',null);bg.css('background-image',null);vinylimg.attr('src','../image/novinyl.png');window.parent.$('#vinyl').css('backgroundImage','url(../image/Vinyl_'+window.parent.type+'.png)')}}})}function getLyric(idx){let duration=$('#audio')[0].duration;setTimeout(function(){if(isNaN(duration)){getLyric(idx)}else{duration=Math.round(duration)*1000;let song=table.songs[idx];let url=urlEncode('/getLyric?name='+song.artist+' - '+song.title+'&duration='+duration);$.ajax({url:url,success:function(result){if(result!='-1'){result=new Base64().decode(result);window.parent[1].songlrc=result;window.parent[1].parseLyric()}else{window.parent[1].songlrc="";window.parent[1].clearplay()}}})}},100)}function playmode(mode,num){let length=0;switch(mode){case 1:case 2:length=table.songs.length;if(num>length-1){num=0;lastli=length-1}else if(num<0){num=length-1;lastli=0}this.num=num;break;case 3:length=table.songs.length;this.num=Math.round(Math.random()*(length-1));$("html,body").animate({"scrollTop":this.num*39-196});break;default:break}}function playgif(val){if(val){$("#li"+(num+1)+" td:eq(0)").attr("value","gif_"+window.parent.type)}else{$("#li"+(num+1)+" td:eq(0)").attr("value","png_"+window.parent.type)}}function convertTime(time){var hou=Math.floor(time/3600);if(hou==0)hou="";else if(hou<10)hou="0"+hou+":";else if(hou>=10)hou=hou+":";var min=Math.floor(time/60-Math.floor(time/3600)*60);if(min<10)min="0"+min;var sec=Math.round(time)%60;if(sec<10)sec="0"+sec;return hou+""+min+":"+sec}function urlEncode(url){url=encodeURIComponent(url);url=url.replace(/\%3A/g,":");url=url.replace(/\%2F/g,"/");url=url.replace(/\%3F/g,"?");url=url.replace(/\%3D/g,"=");url=url.replace(/\%26/g,"&");return url}